import json
import os
from flask_login import UserMixin
from werkzeug.security import generate_password_hash, check_password_hash
import uuid

class User(UserMixin):
    def __init__(self, id, nama, email, password_hash, favorites=None, profile_image=None, role='pengguna'):
        self.id = id
        self.nama = nama
        self.email = email
        self.password_hash = password_hash
        self.favorites = favorites or []
        self.profile_image = profile_image or 'https://via.placeholder.com/40x40/6b7280/ffffff?text=User'
        self.role = role

    def check_password(self, password):
        return check_password_hash(self.password_hash, password)

    @staticmethod
    def get_users_file():
        return 'data/users.json'

    @classmethod
    def get_all(cls):
        if not os.path.exists(cls.get_users_file()):
            return []
        with open(cls.get_users_file(), 'r', encoding='utf-8') as f:
            users_data = json.load(f)
            return [cls(**user_data) for user_data in users_data]

    @classmethod
    def get(cls, user_id):
        users = cls.get_all()
        for user in users:
            if user.id == user_id:
                return user
        return None

    @classmethod
    def get_by_email(cls, email):
        users = cls.get_all()
        for user in users:
            if user.email == email:
                return user
        return None

    def save(self):
        users = User.get_all()
        # Update existing user or add new user
        for i, user in enumerate(users):
            if user.id == self.id:
                users[i] = self
                break
        else:
            users.append(self)

        # Save to file
        users_data = []
        for user in users:
            users_data.append({
                'id': user.id,
                'nama': user.nama,
                'email': user.email,
                'password_hash': user.password_hash,
                'favorites': user.favorites,
                'profile_image': user.profile_image,
                'role': getattr(user, 'role', 'pengguna')
            })

        with open(self.get_users_file(), 'w', encoding='utf-8') as f:
            json.dump(users_data, f, indent=2, ensure_ascii=False)

    @classmethod
    def create(cls, nama, email, password):
        password_hash = generate_password_hash(password)
        user = cls(
            id=str(uuid.uuid4()),
            nama=nama,
            email=email,
            password_hash=password_hash
        )
        user.save()
        return user

    def add_favorite(self, book_id):
        if book_id not in self.favorites:
            self.favorites.append(book_id)
            self.save()

    def remove_favorite(self, book_id):
        if book_id in self.favorites:
            self.favorites.remove(book_id)
            self.save()

    def is_favorite(self, book_id):
        return book_id in self.favorites

    def is_admin(self):
        return self.role == 'admin'

    def is_pengguna(self):
        return self.role == 'pengguna'

    def update(self, nama=None, email=None, role=None, password=None):
        """Update user information"""
        if nama is not None:
            self.nama = nama
        if email is not None:
            self.email = email
        if role is not None:
            self.role = role
        if password is not None:
            self.password_hash = generate_password_hash(password)
        self.save()

    @classmethod
    def delete(cls, user_id):
        """Delete a user by ID"""
        users = cls.get_all()
        users = [user for user in users if user.id != user_id]

        # Save updated users list
        users_data = []
        for user in users:
            users_data.append({
                'id': user.id,
                'nama': user.nama,
                'email': user.email,
                'password_hash': user.password_hash,
                'favorites': user.favorites,
                'profile_image': user.profile_image,
                'role': getattr(user, 'role', 'pengguna')
            })

        # Ensure directory exists
        os.makedirs(os.path.dirname(cls.get_users_file()), exist_ok=True)

        with open(cls.get_users_file(), 'w', encoding='utf-8') as f:
            json.dump(users_data, f, indent=2, ensure_ascii=False)

static/css/style.css.content:
/* Custom CSS for RekoBuku */

:root {
    --primary-color: #4f46e5;
    --secondary-color: #6b7280;
    --success-color: #059669;
    --warning-color: #d97706;
    --danger-color: #dc2626;
    --light-bg: #f8fafc;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
    background-color: var(--light-bg);
}

/* Navigation */
.navbar-brand {
    font-size: 1.5rem;
    font-weight: 700;
}

.nav-link {
    font-weight: 500;
    transition: color 0.2s ease;
}

.nav-link:hover {
    color: var(--primary-color) !important;
}

.nav-link.active {
    color: var(--primary-color) !important;
    font-weight: 600;
}

/* Book Cards */
.book-card {
    transition: transform 0.2s ease, box-shadow 0.2s ease;
    border: none;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.book-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 24px rgba(0,0,0,0.15);
}

.book-cover {
    height: 200px;
    object-fit: cover;
    border-radius: 0.375rem 0.375rem 0 0;
}

.book-card .card-title {
    font-size: 0.9rem;
    font-weight: 600;
    line-height: 1.3;
    height: 2.6rem;
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
}

.book-card .card-text {
    font-size: 0.8rem;
    margin-bottom: 0.5rem;
}

/* Buttons */
.btn {
    font-weight: 500;
    border-radius: 0.5rem;
    transition: all 0.2s ease;
}

.btn-primary {
    background-color: var(--primary-color);
    border-color: var(--primary-color);
}

.btn-primary:hover {
    background-color: #3730a3;
    border-color: #3730a3;
    transform: translateY(-1px);
}

.btn-outline-primary:hover {
    background-color: var(--primary-color);
    border-color: var(--primary-color);
    transform: translateY(-1px);
}

.btn-outline-danger:hover {
    transform: translateY(-1px);
}

/* Genre badges */
.badge {
    font-size: 0.7rem;
    font-weight: 500;
}

/* Cards */
.card {
    border: none;
    border-radius: 0.75rem;
    box-shadow: 0 4px 16px rgba(0,0,0,0.1);
}

.card-body {
    padding: 1.5rem;
}

/* Forms */
.form-control {
    border-radius: 0.5rem;
    border: 1.5px solid #e5e7eb;
    padding: 0.75rem 1rem;
    transition: border-color 0.2s ease, box-shadow 0.2s ease;
}

.form-control:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 0.2rem rgba(79, 70, 229, 0.15);
}

.form-label {
    font-weight: 600;
    color: #374151;
    margin-bottom: 0.5rem;
}

/* Alerts */
.alert {
    border: none;
    border-radius: 0.75rem;
    font-weight: 500;
}

/* Hero section */
.display-4 {
    font-weight: 800;
    letter-spacing: -0.025em;
}

.lead {
    font-size: 1.125rem;
    font-weight: 400;
}

/* Profile image */
.rounded-circle {
    border: 2px solid #e5e7eb;
}

/* Mobile Navigation Styles */
.mobile-header {
    position: sticky;
    top: 0;
    z-index: 1020;
}

.mobile-profile img {
    border: 2px solid var(--primary-color);
}

/* Mobile Bottom Navigation */
.mobile-bottom-nav {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    background: white;
    border-top: 1px solid #e5e7eb;
    padding: 0.5rem 0;
    z-index: 1030;
    box-shadow: 0 -4px 20px rgba(0,0,0,0.1);
    display: flex;
    justify-content: space-around;
    align-items: center;
    height: 70px;
}

.mobile-bottom-nav .nav-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 0.5rem 0.75rem;
    text-decoration: none;
    color: #6b7280;
    transition: all 0.2s ease;
    border-radius: 0.5rem;
    min-width: 60px;
}

.mobile-bottom-nav .nav-item:hover {
    color: var(--primary-color);
    background-color: rgba(79, 70, 229, 0.1);
    text-decoration: none;
}

.mobile-bottom-nav .nav-item.active {
    color: var(--primary-color);
    background-color: rgba(79, 70, 229, 0.15);
}

.mobile-bottom-nav .nav-item i {
    font-size: 1.2rem;
    margin-bottom: 0.25rem;
}

.mobile-bottom-nav .nav-item span {
    font-size: 0.75rem;
    font-weight: 500;
    text-align: center;
}

/* Main content margin for mobile with bottom nav */
@media (max-width: 991px) {
    .main-content {
        padding-bottom: 20px; /* Reduced since body also has padding */
    }
}

/* Body padding for mobile to prevent footer overlap - only when authenticated */
@media (max-width: 991px) {
    body.authenticated {
        padding-bottom: 80px;
    }

    body:not(.authenticated) {
        padding-bottom: 0;
    }
}

/* Horizontal Scroll for Books */
.horizontal-scroll {
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
    scrollbar-width: none; /* Firefox */
    scroll-snap-type: x mandatory;
    scroll-padding-left: 0.5rem;
}

/* Hide scrollbar for WebKit browsers */
.horizontal-scroll::-webkit-scrollbar {
    display: none;
}

.book-cards-container {
    display: flex;
    gap: 1rem;
    padding: 0.5rem;
    min-width: max-content;
}

.book-card-mobile {
    flex: 0 0 auto;
    scroll-snap-align: start;
    width: 140px; /* Fixed width for better appearance */
}

/* Mobile specific adjustments */
@media (max-width: 991px) {
    .book-card-mobile {
        width: 140px; /* Consistent width on mobile */
    }
}

/* Very small screens */
@media (max-width: 480px) {
    .book-card-mobile {
        width: 150px; /* Made wider for better proportion */
    }

    .book-cards-container {
        gap: 0.75rem; /* Reduce gap on small screens */
        padding: 0.5rem 0.75rem; /* Reduce horizontal padding */
    }

    .book-cover-mobile {
        height: 180px; /* Reduced height for better proportion on small screens */
    }
}

.book-card-mobile .card {
    width: 100%;
    border-radius: 1rem;
}

.book-cover-mobile {
    height: 200px;
    object-fit: cover;
    border-radius: 1rem 1rem 0 0;
}

.book-card-mobile .card-body {
    padding: 1rem 0.75rem;
}

.book-card-mobile .card-title {
    font-size: 0.85rem;
    line-height: 1.2;
    height: 2.4rem;
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
}

.book-card-mobile .card-text {
    font-size: 0.75rem;
}

.book-card-mobile .badge {
    font-size: 0.65rem;
    padding: 0.25rem 0.4rem;
}

.book-card-mobile .btn {
    font-size: 0.75rem;
    padding: 0.4rem 0.75rem;
    border-radius: 0.5rem;
}

/* Desktop book card styles */
.book-card-desktop {
    flex: 0 0 auto;
    scroll-snap-align: start;
    width: 260px; /* Made wider for better desktop experience */
}

.book-cover-desktop {
    height: 240px; /* Reduced height for better proportion */
    object-fit: cover;
    border-radius: 0.375rem 0.375rem 0 0;
}

.book-card-desktop .card {
    width: 100%;
    border-radius: 1rem;
}

.book-card-desktop .card-body {
    padding: 1.25rem;
}

.book-card-desktop .card-title {
    font-size: 0.95rem;
    line-height: 1.3;
    height: 2.6rem;
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    font-weight: 600;
}

.book-card-desktop .card-text {
    font-size: 0.85rem;
}

.book-card-desktop .badge {
    font-size: 0.7rem;
    padding: 0.25rem 0.5rem;
}

.book-card-desktop .btn {
    font-size: 0.8rem;
    padding: 0.5rem 0.8rem;
    border-radius: 0.5rem;
}

/* Desktop responsive improvements */
@media (min-width: 992px) {
    .main-content {
        padding-bottom: 0;
    }

    body {
        padding-bottom: 0;
    }

    /* Enable horizontal scroll on desktop */
    .horizontal-scroll {
        display: block;
        overflow-x: auto;
        cursor: grab;
    }

    .horizontal-scroll:active {
        cursor: grabbing;
    }

    /* Desktop book cards container */
    .book-cards-container {
        display: flex;
        gap: 1.5rem;
        padding: 0.5rem 0.25rem;
    }

    /* Improve scrolling experience on desktop */
    .horizontal-scroll {
        padding-bottom: 10px;
        margin-bottom: -10px;
    }
}

/* Footer */
footer {
    margin-top: auto;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .book-card .card-title {
        font-size: 0.85rem;
        height: auto;
        -webkit-line-clamp: 3;
    }

    .display-4 {
        font-size: 2rem;
    }

    .lead {
        font-size: 1rem;
    }
}

@media (max-width: 576px) {
    .container {
        padding-left: 1rem;
        padding-right: 1rem;
    }

    .book-cover {
        height: 180px;
    }
}

/* Animation for favorite button */
.book-favorite-btn {
    position: relative;
    overflow: hidden;
}

.book-favorite-btn.favorited {
    background-color: var(--danger-color);
    border-color: var(--danger-color);
    color: white;
}

/* Loading state */
.btn-loading {
    position: relative;
    color: transparent;
}

.btn-loading::after {
    content: "";
    position: absolute;
    width: 16px;
    height: 16px;
    top: 50%;
    left: 50%;
    margin-left: -8px;
    margin-top: -8px;
    border: 2px solid #ffffff;
    border-radius: 50%;
    border-top-color: transparent;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

/* Admin Panel Styles */
.admin-sidebar {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
