}

.mobile-book-item:hover {
    background-color: rgba(79, 70, 229, 0.02);
}

.mobile-book-cover {
    width: 50px;
    height: 75px;
    object-fit: cover;
    border-radius: 0.5rem;
    box-shadow: 0 2px 8px rgba(0,0,0,0.15);
}

.mobile-book-title {
    font-size: 0.95rem;
    font-weight: 600;
    color: #1f2937;
    line-height: 1.3;
    margin-bottom: 0.25rem;
}

.mobile-book-author {
    font-size: 0.85rem;
    color: #6b7280;
    margin-bottom: 0.5rem;
}

.mobile-book-genres {
    margin-bottom: 0.5rem;
}

.mobile-genre-badge {
    font-size: 0.7rem;
    padding: 0.25rem 0.5rem;
    background-color: var(--primary-color);
    color: white;
