    border-radius: 0.75rem;
    box-shadow: 0 8px 32px rgba(0,0,0,0.15);
}

.book-detail-content {
    padding-left: 2rem;
}

@media (max-width: 768px) {
    .book-detail-content {
        padding-left: 0;
        margin-top: 1.5rem;
    }
}

